import React, { useState } from 'react';
import DayPickerInput from 'react-day-picker/DayPickerInput';
import 'react-day-picker/lib/style.css';
import dateFnsFormat from 'date-fns/format';

const FORMAT = 'dd/MM/yyyy';

function formatDate(date, format, locale){
    return dateFnsFormat(date, format, {locale});
}

const AddTask = ({onCancel, onAddTask}) => {

    const [task, setTask] = useState('');
    const [date, setDate] = useState(null);

    return (
        <div className='add-task-dialog'>
            <input value={task} onChange={(event) => setTask(event.target.value)}></input>
            <div className='add-task-actions-container'>
                <div className='btns-container'>
                    <button disabled={!task} className='add-btn' onClick={() => {onAddTask(task, date); onCancel(); setTask('')}}>Add Task</button>
                    <button className='cancel-btn' onClick={() => {onCancel(); setTask('')}}>Cancel</button>
                </div>
                <div className='icon-container'>
                    <DayPickerInput
                     onDayChange={(day) => setDate(day)}
                     placeholder={`${dateFnsFormat(new Date(), FORMAT)}`}
                     formatDate={formatDate}
                     format={FORMAT}
                     dayPickerProps={{
                        modifiers: {
                            disabled: [{before:new Date()}],
                        },
                     }}
                    />
                </div>
            </div>
        </div>
    );
};

const TASKS_HEADER_MAPPING = {
    INBOX: 'Inbox',
    TODAY: 'Today',
    NEXT: 'Next'
}

const TaskItems = ({selectedTab, tasks}) => {}

const Tasks = ({selectedTab}) => {

    const [showAddTask, setShowAddTask] = useState(false);
    const [tasks, setTasks] = useState([]);

    const addNewTask = (text, date) => {
        const newTaskItem = {text, date: date || new Date()};
        setTasks((prevState) => [...prevState, newTaskItem]);
    };

    return (
        <div className='tasks'>
            <h1>{TASKS_HEADER_MAPPING[selectedTab]}</h1>
            <div className='add-task-btn' onClick={() => setShowAddTask((prevState) => !prevState)}>
                <span className='plus'>+</span>
                <span className='add-task-text'>Add Task</span>
            </div>
            {showAddTask && <AddTask onAddTask={addNewTask} onCancel={() => setShowAddTask(false)} />}
            {tasks.length > 0 ?
            tasks.map(task => (
                <p>{task.text}{" "}{dateFnsFormat(new Date(task.date), FORMAT)}</p>
            )) : (
            <p>No Tasks</p>)}
        </div>
    );
};

export default Tasks;
